import java.util.*;class Main{public static void main(String[] args) {        			    Scanner s =new Scanner(System.in);        System.out.print("Enter the size of the array: ");    int x=s.nextInt();    Contact array1 []; 	array1 = new Contact [x];	            for (int i = 0; i < x; i++) {            System.out.println("Enter data for contact " + (i + 1) + ":");            System.out.print("Name: ");            String name = s.next();            System.out.print("Phone number: ");            String phoneNumber = s.next();            System.out.print("Category (Friend, Family, or Work): ");            String cat = s.next();            while (!cat.equals("Friend") && !cat.equals("Family") && !cat.equals("Work")) {                System.out.println("Invalid category. Please enter Friend, Family, or Work.");                cat = s.next();            }            array1[i] = new Contact(name, phoneNumber, cat);        }               System.out.print("Enter name to search for :");           String search = s.next();                      for(Contact contact:array1){                if (contact.getName().equals(search)) {                System.out.println("Phone number for " + search + ": " + contact.getPhoneNumber() + ": " + contact.getCat());                break;           }else{                                             System.out.println("contact not found");                          }               }	 }  }